bool bmysqlinsert(const QString &amp;amp;name, int age)
{
    QSqlQuery query;
    //ç»‘å®šå€¼ï¼Œå¾…å®šå˜é‡é»˜è®¤é—®å·ï¼Œå¯è‡ªå®šä¹‰
    query.prepare(R&#34;(INSERT INTO my_table(name,age) VALUES(?,?);)&#34;);
