        qDebug()&amp;lt;&amp;lt;&#34;insert successful!&#34;;
    }else{
        //æ‰“å°sqlè¯­å¥é”™è¯¯ä¿¡æ¯
        qDebug()&amp;lt;&amp;lt;&#34;init table error&#34;&amp;lt;&amp;lt;query.lastError();
        return false;
    }
    return true;
}

