&lt;h3 id=&#34;ä¾¦å¬æŒ‰é”®&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#ä¾¦å¬æŒ‰é”®&#34;&gt;#&lt;/a&gt; ä¾¦å¬æŒ‰é”®&lt;/h3&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void MainWindow::on_pushButton_Socket_Server_Connect_clicked()
{
    if (ui-&amp;gt;pushButton_Socket_Server_Connect-&amp;gt;text() == tr(&#34;ä¾¦å¬&#34;)) {
        // ä»è¾“å…¥ç«¯è·å–ç«¯å£å·
        int port = ui-&amp;gt;comboBoxData_Socket_Server_COM-&amp;gt;currentText().toInt();

        // ä¾¦å¬æŒ‡å®šçš„ç«¯å£
        if(!server-&amp;gt;listen(QHostAddress::Any, port)) {
            // è‹¥å‡ºé”™ï¼Œåˆ™è¾“å‡ºé”™è¯¯ä¿¡æ¯
            QMessageBox::information(this, tr(&#34;é”™è¯¯&#34;), server-&amp;gt;errorString(), QMessageBox::Yes);
            return;
        }
        else {
            // ä¿®æ”¹æŒ‰é”®æ–‡å­—
            ui-&amp;gt;pushButton_Socket_Server_Connect-&amp;gt;setText(&#34;å–æ¶ˆä¾¦å¬&#34;);
            QPalette pe;
            pe.setColor(QPalette::WindowText, Qt::red);
            ui-&amp;gt;label_Socket_Server_Status-&amp;gt;setPalette(pe);
            ui-&amp;gt;label_Socket_Server_Status-&amp;gt;setText(&#34;æœåŠ¡å™¨è¿è¡Œä¸­...&#34;);
        }

        //-----------------------------å¯åŠ¨FRPS-----------------------------
        //è¿™é‡Œæˆ‘æ˜¯è‡ªå·±æ­å»ºäº†å†…ç½‘ç©¿é€ï¼Œå°†æœ¬åœ°ç«¯å£æ˜ å°„åˆ°æœåŠ¡å™¨çš„å…¬ç½‘ä¸Šï¼Œç›¸å½“äºå¯ä»¥è”ç½‘æ§åˆ¶äº†
        bfrpsstart(true);
    }
    else {
        // å¦‚æœæ­£åœ¨è¿æ¥......
        if(socket-&amp;gt;state() == QAbstractSocket::ConnectedState) {
            // å…³é—­è¿æ¥
            socket-&amp;gt;disconnectFromHost();
        }
        // å–æ¶ˆä¾¦å¬
        server-&amp;gt;close();
        // ä¿®æ”¹æŒ‰é”®æ–‡å­—
        ui-&amp;gt;pushButton_Socket_Server_Connect-&amp;gt;setText(&#34;ä¾¦å¬&#34;);
        QPalette pe;
        pe.setColor(QPalette::WindowText, Qt::blue);
        ui-&amp;gt;label_Socket_Server_Status-&amp;gt;setPalette(pe);
        ui-&amp;gt;label_Socket_Server_Status-&amp;gt;setText(&#34;æœåŠ¡å™¨æœªæ‰“å¼€&#34;);

        ui-&amp;gt;tableWidget_Socket_Server-&amp;gt;clear();
        //-----------------------------å…³é—­FRPS-----------------------------
        bfrpsstart(false);
    }
}
