&lt;h2 id=&#34;qstringè½¬uint8_t&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#qstringè½¬uint8_t&#34;&gt;#&lt;/a&gt; QString è½¬ uint8_t&lt;/h2&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;unsigned char puchar[1024];
uint8_t *qstring2uint8(QString string){
    char * pchar;
    QByteArray temparr;
    int len;
    temparr = string.toLatin1();
    pchar = temparr.data();
    len = temparr.size();
    //å°†å¾—åˆ°çš„charç±»å‹è½¬ä¸ºuchar
    convertStrToUnChar(pchar,puchar,len);
    return puchar;
}
