    query.prepare(R&#34;(UPDATE my_table SET age=:age WHERE name=:name;)&#34;);
    query.bindValue(&#34;:name&#34;,name);//é€šè¿‡è‡ªå®šä¹‰çš„åˆ«åæ¥æ›¿ä»£
    query.bindValue(&#34;:age&#34;,age);
